import { useState, useEffect } from "react";
import itemServices from "./services/itemServices";

import ItemForm from "./components/itemForm";

/* --- --- --- --- --- VARIABLES --- --- --- --- --- --- */

const App = () => {
  const [items, setItems] = useState([]);

  /*
  useEffect(() => {
    inventoryServices.getAll().then((response) => {
      setItems(response.data);
    });
  }, []);
*/

  const [newName, setNewName] = useState("");
  const [newUnit, setNewUnit] = useState("");
  const [newPrice, setNewPrice] = useState("");
  const [newExpiry, setNewExpiry] = useState("");
  const [newStocks, setNewStocks] = useState("");
  //const [prodPic, setProdPic] = useState("");
  const [message, setMessage] = useState(null);

  /* --- --- --- --- --- EVENT HANDLERS --- --- --- --- --- --- */

  const handleName = (event) => {
    setNewName(event.target.value);
  };

  const handleUnit = (event) => {
    setNewUnit(event.target.value);
  };

  const handlePrice = (event) => {
    setNewPrice(event.target.value);
  };

  const handleExpiry = (event) => {
    setNewPrice(event.target.value);
  };

  const handleStocks = (event) => {
    setNewStocks(event.target.value);
  };

  /* --- --- --- --- --- FUNCTIONS --- --- --- --- --- --- */

  const generateId = () => {
    return Math.floor(Math.random() * 9999999999999999);
  };

  const addNewItem = (event) => {
    const newItem = {
      name: newName,
      unit: newUnit,
      price: newPrice,
      expiry: newExpiry,
      stocks: newStocks,
      id: generateId(),
    };
    event.preventDefault();

    setItems(items.concat(newItem));

    itemServices.create(newItem).then((response) => {
      setItems(items.concat(response.data));
    });
    setNewName("");
    setNewUnit("");
    setNewPrice("");
    setNewExpiry("");
    setNewStocks("");
  };

  /* --- --- --- --- --- APP RENDER --- --- --- --- --- --- */

  return (
    <div>
      <div id="header">
        <div className="logo">
          <h1 className="title">Inventory Portal</h1>
        </div>
      </div>
      <ItemForm />
    </div>
  );
};

export default App;
