import trash from "../images/trash.svg";
//import edit from "../images/edit.svg";

const Items = ({ items, handleDelete }) => {
  return (
    <div id="itemWrapper">
      {items.map((item) => {
        return (
          <div className="item" key={item.id}>
            <h3>{item.name}</h3>
            <p>Unit of Measure:&nbsp; {item.unit}</p>
            <p>Price:&nbsp; â‚± {item.price}</p>
            <p>Expiry Date:&nbsp; {item.expiry}</p>
            <p>Stocks:&nbsp; {item.stocks}</p>
            <p>Total Value:&nbsp; {item.totalValue}</p>
            <img
              src={trash}
              alt=""
              className="trash"
              id={item.id}
              name={item.name}
              onClick={handleDelete}
            />
          </div>
        );
      })}
    </div>
  );
};
