import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";

const EditForm = ({
  items,
  id,
  idEdit,
  editItem,
  showEdit,
  setShowEdit,
  handleNewName,
  handleNewUnit,
  handleNewPrice,
  handleNewExpiry,
  handleNewStocks,
  handleItemPic,
  triggerEdit,
}) => {
  const itemToChange = items.find((item) => item.id === id);
  console.log(itemToChange);

  const handleCloseEdit = () => {
    setShowEdit(false);
  };

  return (
    <div className="newItem">
      <div id="newItemBtn">
        <Button variant="dark" size="sm" onClick={triggerEdit} id={this.id}>
          EDIT
        </Button>
      </div>
      <Modal show={showEdit} onHide={handleCloseEdit}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Item</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form /*onSubmit={props.editItem}*/>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Item Name:</Form.Label>
              <Form.Control
                type="text"
                //value={itemToChange.name}
                onChange={handleNewName}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput2">
              <Form.Label>Unit of Measure:</Form.Label>
              <Form.Control
                type="text"
                //value={itemToChange.unit}
                onChange={handleNewUnit}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput3">
              <Form.Label>Price:</Form.Label>
              <Form.Control
                type="number"
                //value={itemToChange.price}
                onChange={handleNewPrice}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput4">
              <Form.Label>Expiry Date:</Form.Label>
              <Form.Control
                type="date"
                //value={itemToChange.expiry}
                onChange={handleNewExpiry}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput5">
              <Form.Label>Stocks:</Form.Label>
              <Form.Control
                type="number"
                //value={itemToChange.stocks}
                onChange={handleNewStocks}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput6">
              <Form.Label>Image URL:</Form.Label>
              <Form.Control
                type="url"
                //value={itemToChange.photo}
                onChange={handleItemPic}
              />
            </Form.Group>
            <div id="buttonWrapper">
              <Button variant="primary" type="submit" onClick={handleCloseEdit}>
                Add Item
              </Button>
              <Button variant="secondary" onClick={handleCloseEdit}>
                Close
              </Button>
            </div>
          </Form>
        </Modal.Body>
        <Modal.Footer></Modal.Footer>
      </Modal>
    </div>
  );
};

export default EditForm;
