import { useState, useEffect } from "react";
import itemServices from "./services/itemServices";

import ItemForm from "./components/itemForm";
import Items from "./components/items";

/* --- --- --- --- --- VARIABLES --- --- --- --- --- --- */

const App = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    itemServices.getAll().then((response) => {
      setItems(response.data);
    });
  }, []);

  const [newName, setNewName] = useState("");
  const [newUnit, setNewUnit] = useState("");
  const [newPrice, setNewPrice] = useState("");
  const [newExpiry, setNewExpiry] = useState("");
  const [newStocks, setNewStocks] = useState("");
  //const [prodPic, setProdPic] = useState("");
  const [message, setMessage] = useState(null);

  /* --- --- --- --- --- EVENT HANDLERS --- --- --- --- --- --- */

  const handleNewName = (event) => {
    setNewName(event.target.value);
  };

  const handleNewUnit = (event) => {
    setNewUnit(event.target.value);
  };

  const handleNewPrice = (event) => {
    setNewPrice(event.target.value);
  };

  const handleNewExpiry = (event) => {
    setNewPrice(event.target.value);
  };

  const handleNewStocks = (event) => {
    setNewStocks(event.target.value);
  };

  /* --- --- --- --- --- FUNCTIONS --- --- --- --- --- --- */

  const generateId = () => {
    return Math.floor(Math.random() * 9999999999999999);
  };

  const addNewItem = (event) => {
    const newItem = {
      name: newName,
      unit: newUnit,
      price: newPrice,
      expiry: newExpiry,
      stocks: newStocks,
      id: generateId(),
    };
    event.preventDefault();

    setItems(items.concat(newItem));

    itemServices.create(newItem).then((response) => {
      setItems(items.concat(response.data));
    });
    setNewName("");
    setNewUnit("");
    setNewPrice("");
    setNewExpiry("");
    setNewStocks("");
  };

  /* --- --- --- --- --- APP RENDER --- --- --- --- --- --- */

  return (
    <div>
      <div id="header">
        <div className="logo">
          <h1 className="title">Inventory Portal</h1>
        </div>
      </div>
      <Items items={items} />
      <ItemForm
        addNewItem={addNewItem}
        newName={newName}
        newUnit={newUnit}
        newPrice={newPrice}
        newExpiry={newExpiry}
        newStocks={newStocks}
        handleNewName={handleNewName}
        handleNewUnit={handleNewUnit}
        handleNewPrice={handleNewPrice}
        handleNewExpiry={handleNewExpiry}
        handleNewStocks={handleNewStocks}
      />
    </div>
  );
};

export default App;
